import React from 'react';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import '@trendmicro/react-modal/dist/react-modal.css';
import Modal from '@trendmicro/react-modal';

class AddService extends React.Component {
    constructor(props) {
        super(props);
        this.state={
            service: props.group_working && props.editServiceItem ? props.group_working:{
                "name":"",
                "details":"",
                "priceFrom":'',
                "priceTo":'',
                "duration":900,
                "specialPrice":0,
                "specialDescription":"",
                "currency":"BYN",
                "onlineBooking":true,
                "staffs":[]
            },
            editServiceItem: props.editServiceItem,
            staffs: props.staffs && props.staffs,
            allStaffs: props.staffs && props.staffs,
            group: props.group && props.group,
            services: props.services && props.services
        };

        this.handleChange = this.handleChange.bind(this);
        this.handleChangePrice = this.handleChangePrice.bind(this);
        this.toggleChange = this.toggleChange.bind(this);
        this.updateService = this.updateService.bind(this);
        this.closeModal = this.closeModal.bind(this);
        this.addService = this.addService.bind(this);
        this.handleSearch = this.handleSearch.bind(this);

    }

    componentWillReceiveProps(newProps) {
        if ( JSON.stringify(this.props.services) !==  JSON.stringify(newProps.services)) {
            this.setState({services:newProps.services});
        }
    }

    render() {
        const {service, editServiceItem, colors, staffs, group, allStaffs, services}=this.state;

        return (
            <Modal size="md" onClose={this.closeModal} showCloseButton={false} className="mod">
                <div className="new-service-modal">
                {service &&
                <div className="modal-dialog modal-dialog-lg modal-dialog-centered">
                    <div className="modal-content">
                        <div className="modal-header">
                            {
                                editServiceItem ? <h4 className="modal-title">Редактирование услуги</h4>
                                    : <h4 className="modal-title">Новая услуга</h4>

                            }
                            <button type="button" className="close" onClick={this.closeModal}></button>
                            {/*<img src={`${process.env.CONTEXT}public/img/icons/cancel.svg`} alt="" className="close" onClick={this.closeModal}*/}
                            {/*     style={{margin:"13px 5px 0 0"}}/>*/}
                        </div>
                        <div className="form-group pl-4 pr-4">
                            <div className="row">
                                <div className="col-lg-7">
                                    <p className="title mb-2">Общая информация</p>
                                    {group &&
                                    <div className="select-color mb-3 border-color">
                                        <li><a><span
                                            className={group.color.toLowerCase() + " " + 'color-circle ml-0'}/><span
                                            className={group.color.toLowerCase()}><span
                                            className="items-color"><span>{group.name}</span></span></span></a>
                                        </li>
                                    </div>
                                    }
                                    <div className="input_limited_wrapper_name">
                                    <p>Название услуги</p>
                                        <input type="text" placeholder="" name="name" maxLength="60"
                                               value={service.name} onChange={this.handleChange} className={((service.priceFrom || service.specialPrice) && service.name===''? ' redBorder' : '')}/>
                                            <span className="input_limited_name">
                                                {service.name.length}/60
                                            </span>
                                    </div>
                                    <div className="row">
                                        <div className="col-xl-6 input_limited_wrapper_description">
                                            <p>Детали услуги</p>
                                            <input type="text" placeholder="" maxLength="120"
                                                   name="details" value={service.details}
                                                   onChange={this.handleChange}/>
                                            <span className="input_limited_description">
                                                {service.details.length}/120
                                            </span>
                                        </div>
                                        <div className="col-xl-6">
                                            <p>Местная валюта</p>

                                            <select className="custom-select" onChange={this.handleChange} name="currency"
                                                    value={service.currency}>
                                                <option value="BYN">BYN</option>
                                                <option value="UAH">UAH</option>
                                                <option value="EUR">EUR</option>
                                                <option value="USD">USD</option>
                                                <option value="RUR">RUB</option>
                                                <option value="GBP">GBP</option>
                                            </select>
                                        </div>
                                    </div>
                                    <p className="title mt-2  mb-2">Настройки услуги</p>
                                    <p>Длительность</p>
                                    <select className="custom-select" onChange={this.handleChange} name="duration"
                                            value={service.duration}>
                                        <option value="900">15 мин</option>
                                        <option value="1800">30 мин</option>
                                        <option value="2700">45 мин</option>
                                        <option value="3600">1 ч 00 мин</option>
                                        <option value="4500">1 ч 15 мин</option>
                                        <option value="5400">1 ч 30 мин</option>
                                        <option value="6300">1 ч 45 мин</option>
                                        <option value="7200">2 ч 00 мин</option>
                                        <option value="8100">2 ч 15 мин</option>
                                        <option value="9000">2 ч 30 мин</option>
                                        <option value="9900">2 ч 45 мин</option>
                                        <option value="10800">3 ч 00 мин</option>
                                        <option value="12600">3 ч 30 мин</option>
                                        <option value="14400">4 ч 00 мин</option>
                                        <option value="15300">4 ч 15 мин</option>
                                        <option value="16200">4 ч 30 мин</option>
                                        <option value="17100">4 ч 45 мин</option>
                                        <option value="18000">5 ч 00 мин</option>
                                        <option value="18900">5 ч 15 мин</option>
                                        <option value="19800">5 ч 30 мин</option>
                                        <option value="20700">5 ч 45 мин</option>
                                        <option value="21600">6 ч 00 мин</option>
                                        <option value="22500">6 ч 15 мин</option>
                                        <option value="23400">6 ч 30 мин</option>
                                        <option value="24300">6 ч 45 мин</option>
                                        <option value="25200">7 ч 00 мин</option>
                                        <option value="26100">7 ч 15 мин</option>
                                        <option value="27000">7 ч 30 мин</option>
                                        <option value="27900">7 ч 45 мин</option>
                                        <option value="28800">8 ч 00 мин</option>
                                        <option value="29700">8 ч 15 мин</option>
                                        <option value="30600">8 ч 30 мин</option>
                                        <option value="31500">8 ч 45 мин</option>
                                        <option value="32400">9 ч 00 мин</option>
                                        <option value="33300">9 ч 15 мин</option>
                                        <option value="34200">9 ч 30 мин</option>
                                        <option value="35100">9 ч 45 мин</option>
                                        <option value="36000">10 ч 00 мин</option>
                                        <option value="36900">10 ч 15 мин</option>
                                        <option value="37800">10 ч 30 мин</option>
                                        <option value="38700">10 ч 45 мин</option>
                                        <option value="39600">11 ч 00 мин</option>
                                        <option value="40500">11 ч 15 мин</option>
                                        <option value="41400">11 ч 30 мин</option>
                                        <option value="42300">11 ч 45 мин</option>
                                        <option value="43200">12 ч 00 мин</option>
                                        <option value="44100">12 ч 15 мин</option>
                                        <option value="45000">12 ч 30 мин</option>
                                        <option value="45900">12 ч 45 мин</option>
                                        <option value="46800">13 ч 00 мин</option>
                                        <option value="47700">13 ч 15 мин</option>
                                        <option value="48600">13 ч 30 мин</option>
                                        <option value="49500">13 ч 45 мин</option>
                                        <option value="50400">14 ч 00 мин</option>
                                        <option value="51300">14 ч 15 мин</option>
                                        <option value="52200">14 ч 30 мин</option>
                                        <option value="53100">14 ч 45 мин</option>
                                        <option value="54000">15 ч 00 мин</option>
                                    </select>
                                    <p>Цена от</p>
                                    <input type="number" placeholder="" name="priceFrom"
                                           value={service.priceFrom} onChange={this.handleChangePrice} className={(service.specialPrice && service.priceFrom===''? ' redBorder' : '')}/>
                                    <p>Цена до</p>
                                    <input type="number" placeholder="" name="priceTo"
                                           value={service.priceTo} onChange={this.handleChange} className={(service.priceFrom && service.priceFrom!==0 && service.priceTo!=='' && service.priceTo!==0 && parseInt(service.priceTo)<parseInt(service.priceFrom)? ' redBorder' : '')}/>

                                    {/*<p>Специальная цена</p>*/}
                                    {/*<input type="number" placeholder="" onChange={this.handleChange} name="specialPrice"*/}
                                    {/*       value={service.specialPrice===0?'':service.specialPrice}*/}

                                    {/*/>*/}
                                    <div className="row">
                                        <div className="col-sm-12">
                                            <div className="check-box">
                                                <label>
                                                    <input className="form-check-input" type="checkbox"
                                                           checked={service.onlineBooking}
                                                           onChange={this.toggleChange}/>
                                                    <span className="check"/>
                                                    Включить онлайн-запись
                                                </label>
                                            </div>
                                        </div>
                                    </div>
                                    <div className="clearfix"/>
                                    {services && services.status === 200 &&
                                    <p className="alert-success p-1 rounded pl-3 mb-2">Сохранено</p>
                                    }
                                    {services && services.adding &&
                                    <img style={{width: "57px"}}
                                         src="data:image/gif;base64,"/>
                                    }
                                </div>
                                <div className="col-lg-5">
                                    <div className="block-style2 container">
                                        <div className="row">
                                            <div className="col-sm-12"><p className="title mb-2">Сотрудники, оказывающие услугу</p></div>
                                        </div>
                                        {allStaffs && allStaffs.length > 0 &&
                                        <div className="search dropdown row">
                                            <form className="col-sm-12 form-inline" data-toggle="dropdown">
                                                <input type="search" placeholder="Поиск по имени" aria-label="Search"  ref={input => this.search = input} onChange={this.handleSearch}/>
                                                <button className="search-icon" type="button"></button>
                                                {/*<ul className="dropdown-menu" role="menu">*/}
                                                {/*<li role="presentation"><a role="menuitem" tabIndex="-1"*/}
                                                {/*href="#">Валерия Семенова</a></li>*/}
                                                {/*<li role="presentation"><a role="menuitem" tabIndex="-1"*/}
                                                {/*href="#">Светлана Александрова</a>*/}
                                                {/*</li>*/}
                                                {/*<li role="presentation"><a role="menuitem" tabIndex="-1"*/}
                                                {/*href="#">Ольга Шубина</a></li>*/}
                                                {/*<li role="presentation"><a role="menuitem" tabIndex="-1"*/}
                                                {/*href="#">Николай Петров</a></li>*/}
                                                {/*<li role="presentation"><a role="menuitem" tabIndex="-1"*/}
                                                {/*href="#">Олег Смолов</a></li>*/}
                                                {/*</ul>*/}
                                            </form>
                                        </div>
                                        }
                                        <ul className={((service.specialPrice || service.priceFrom) && (service.staffs && service.staffs.length===0) ? 'redBorder clients-list-container' : 'clients-list-container')}>
                                            {staffs && staffs.map((item, keyStaffs) =>
                                                <li className="row" key={keyStaffs}>
                                                    <div className="col-9">
                                                    <span className="img-container">
                                                         <img className="rounded-circle"
                                                              src={item.imageBase64?"data:image/png;base64,"+item.imageBase64:`${process.env.CONTEXT}public/img/image.png`}  alt=""/>
                                                    </span>
                                                        <p>{item.firstName} {item.lastName ? item.lastName : ''}</p>
                                                    </div>
                                                    <div className="col-3 justify-content-end check-box">
                                                        <label>
                                                            <input className="form-check-input" type="checkbox"
                                                                   checked={service.staffs && service.staffs.some((staff) => staff.staffId === item.staffId)}
                                                                   onChange={() => this.toggleChangeStaff(item.staffId)}
                                                            />
                                                            <span className="check"></span>
                                                        </label>
                                                    </div>
                                                </li>
                                            )}
                                        </ul>
                                    </div>
                                </div>

                                <button className={services.adding || service.name==='' || service.priceFrom==='' || (service.priceFrom && service.priceTo!=='' && service.priceTo!==0 && parseInt(service.priceTo)<parseInt(service.priceFrom)) || !service.staffs ||(service.staffs && service.staffs.length===0)?"disabledField button mt-2 mb-2":"button mt-2 mb-2"} type="button"
                                        onClick={!services.adding && service.name!=='' && service.priceFrom && service.priceTo!=='' && service.priceTo!==0 && parseInt(service.priceTo)>=parseInt(service.priceFrom) && service.staffs && service.staffs.length!==0&&(editServiceItem ? this.updateService : this.addService)}>{editServiceItem ? 'Обновить услугу' : 'Добавить услугу'}</button>
                            </div>
                        </div>
                    </div>
                </div>
                }
            </div>
            </Modal>


        )
    }

    handleChange(e) {
        const { name, value } = e.target;
        const { service } = this.state;


        this.setState({service:{...service, [name]: name==='specialPrice' && value==='' ? 0 : value}});


    }

    handleChangePrice(e) {
        const { name, value } = e.target;
        const { service } = this.state;

        this.setState({service:{...service, priceFrom: value, priceTo:value}});
    }

    toggleChange () {
        const { service } = this.state;

        this.setState({service:{...service, onlineBooking: !service.onlineBooking}});
    }

    updateService(){
        const {updateService} = this.props;
        const {service} = this.state;

        return updateService(service);
    };

    addService(){
        const {addService} = this.props;
        const {service} = this.state;

        return addService(service);
    };


    toggleChangeStaff (staff) {
        const { service } = this.state;
        const { dispatch } = this.props;

        const staffs = service.staffs;

        if(!staffs){
            this.setState({service:{...service, staffs: [{staffId:staff}]}});
        }else {

            let deleteFunc = staffs.map(function (o) {
                return o.staffId;
            }).indexOf(staff);


            deleteFunc !== -1 ? staffs.splice(deleteFunc, 1) : staffs.push({staffId: staff})

            this.setState({service: {...service, staffs: staffs}});
        }
    }

    handleSearch () {
        const {allStaffs}= this.state;

        const allStaffsList=allStaffs.filter((item)=>{
            return (item.lastName ? item.lastName : '').toLowerCase().includes(this.search.value.toLowerCase()) ||
                item.firstName.toLowerCase().includes(this.search.value.toLowerCase())
        });

        this.setState({
            search: true,
            staffs: allStaffsList
        });

        if(this.search.value===''){
            this.setState({
                search: true,
                staffs: allStaffs
            })
        }


    }

    closeModal () {
        const {onClose} = this.props;

        return onClose()
    }
}

function mapStateToProps(state) {
    const { alert, services } = state;
    return {
        alert, services
    };
}

AddService.propTypes ={
    group_working: PropTypes.object,
    staffs: PropTypes.array,
    editServiceItem: PropTypes.bool.isRequired,
    updateService: PropTypes.func,
    addService: PropTypes.func,
    onClose: PropTypes.func,
    group: PropTypes.object
};

const connectedApp = connect(mapStateToProps)(AddService);
export { connectedApp as AddService };
